mapper.select:
SELECT o.*, u.username, u.email, u.phone, u.address
FROM orders o
LEFT JOIN users u ON o.user_id = u.id
WHERE o.user_id = #{userId}

    AND o.create_time >= #{startDate}


    AND o.create_time <= #{endDate}

ORDER BY o.create_time DESC

mapper.update:
UPDATE orders
total_amount = #{totalAmount}, status = #{status},
WHERE id = #{id}
